text:
Chart 3: Finalist Age Distribution (Waffle Chart)


Code
ages &lt;- read_csv("data/ifsc_finalists_ages.csv", show_col_types = FALSE)

ages_binned &lt;- ages |&gt;
  mutate(age_group = case_when(
    age &lt; 18 ~ "&lt;18",
    age &lt; 21 ~ "18–20",
    age &lt; 24 ~ "21–23",
    age &lt; 27 ~ "24–26",
    TRUE ~ "27+"
  )) |&gt;
  count(discipline, age_group) |&gt;
  group_by(discipline) |&gt;
  mutate(percent = round(n / sum(n) * 100)) |&gt;
  ungroup()

waffle_data &lt;- ages_binned |&gt;
  filter(discipline == "Speed") |&gt;
  select(age_group, percent) |&gt;
  deframe()

waffle(
  waffle_data,
  rows = 5,
  size = 1,
  colors = RColorBrewer::brewer.pal(5, "Blues"),
  title = "Speed Finalists Skew Younger",
  xlab = "1 square = 1% of Speed Finalists"
)
